export function getHijriDate(date: Date): string {
  // Simple Hijri calendar conversion (approximation)
  // In a real app, you'd use a proper Islamic calendar library

  const gregorianYear = date.getFullYear();
  const gregorianMonth = date.getMonth() + 1;
  const gregorianDay = date.getDate();

  // Approximate conversion (this is not accurate for production use)
  const hijriYear = Math.floor((gregorianYear - 622) * 1.030684) + 1;
  const hijriMonthNames = [
    "Muharram", "Safar", "Rabi' al-awwal", "Rabi' al-thani",
    "Jumada al-awwal", "Jumada al-thani", "Rajab", "Sha'ban",
    "Ramadan", "Shawwal", "Dhu al-Qi'dah", "Dhu al-Hijjah"
  ];

  // Simple month calculation (not accurate)
  const hijriMonth = ((gregorianMonth - 1 + Math.floor(gregorianDay / 29)) % 12);
  const hijriDay = ((gregorianDay - 1) % 29) + 1;

  return `${hijriDay} ${hijriMonthNames[hijriMonth]} ${hijriYear}`;
}

export function getGregorianFromHijri(hijriDateString: string): Date {
  // Simple reverse conversion (approximation)
  // This is a placeholder implementation
  return new Date();
}

export function isImportantIslamicDate(hijriDate: string): boolean {
  const importantDates = [
    "1 Muharram", // Islamic New Year
    "10 Muharram", // Day of Ashura
    "12 Rabi' al-awwal", // Mawlid al-Nabi
    "27 Rajab", // Isra and Mi'raj
    "1 Ramadan", // First day of Ramadan
    "27 Ramadan", // Laylat al-Qadr
    "1 Shawwal", // Eid al-Fitr
    "10 Dhu al-Hijjah", // Eid al-Adha
  ];

  return importantDates.some(date => hijriDate.includes(date));
}

client/src/lib/prayer-times.ts.content:
export interface Prayer {
  name: string;
  time: string;
  icon: any;
}

export function calculateNextPrayer(prayers: Prayer[]) {
  const now = new Date();
  const currentTime = now.getHours() * 60 + now.getMinutes();

  // Convert prayer times to minutes
  const prayerMinutes = prayers.map(prayer => {
    const [hours, minutes] = prayer.time.split(':').map(Number);
    return {
      ...prayer,
      minutes: hours * 60 + minutes
    };
  });

  // Find current and next prayer
  let currentPrayer = null;
  let nextPrayer = null;
  let timeRemaining = 0;

  for (let i = 0; i < prayerMinutes.length; i++) {
    const prayer = prayerMinutes[i];
    const nextIndex = (i + 1) % prayerMinutes.length;
    const nextPrayerTime = prayerMinutes[nextIndex];

    // Check if current time is between this prayer and next
    if (i === prayerMinutes.length - 1) {
      // Last prayer of the day
      if (currentTime >= prayer.minutes) {
        currentPrayer = prayer;
        nextPrayer = prayerMinutes[0]; // First prayer of next day
        timeRemaining = (24 * 60) - currentTime + nextPrayer.minutes;
      }
    } else {
      if (currentTime >= prayer.minutes && currentTime < nextPrayerTime.minutes) {
        currentPrayer = prayer;
        nextPrayer = nextPrayerTime;
        timeRemaining = nextPrayerTime.minutes - currentTime;
        break;
      }
    }
  }

  // If no current prayer found, we're before the first prayer
  if (!currentPrayer && !nextPrayer) {
    nextPrayer = prayerMinutes[0];
    timeRemaining = prayerMinutes[0].minutes - currentTime;
  }

  return { currentPrayer, nextPrayer, timeRemaining };
}

export function formatTimeRemaining(minutes: number): string {
  if (minutes < 0) return "0m";

  const hours = Math.floor(minutes / 60);
  const mins = minutes % 60;

  if (hours > 0) {
    return `${hours}h ${mins}m`;
  }
  return `${mins}m`;
}

